class Solution {
public:
    int isWinner(vector<int>& player1, vector<int>& player2) {
        int n = player1.size();
        int score1 = player1[0], score2 = player2[0];
        int playerOneLastTen = score1 == 10, playerTwoLastTen = score2 == 10;
        for(int i = 1; i < n; i++) {
            score1 += (playerOneLastTen + 1) * player1[i];
            score2 += (playerTwoLastTen + 1) * player2[i];
            playerOneLastTen = player1[i] == 10 || player1[i - 1] == 10;
            playerTwoLastTen = player2[i] == 10 || player2[i - 1] == 10;
        }
        if(score1 == score2) return 0;
        if(score1 > score2) return 1;
        return 2;
    }
};
```

