        for (int i=0; i<player1.length; i++) {
            if (p1 == -1) {
                p1sum+=player1[i];
                if (player1[i] == 10) p1 = i;
            } else {
                if (i-p1 <= 2) {
                    p1sum+=(player1[i]*2);
                    if (player1[i] == 10) p1 = i;
