class Solution {
public:
    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& mat) {
        int m=mat.size(), n=mat[0].size(), size=arr.size(), idx=0;
        unordered_set<int>st;
        unordered_map<int, pair<int,int>>mp;

         for(int i=0;i<m;i++) {
             for(int j=0;j<n;j++) {
                    mp[mat[i][j]]={i,j};
                }
         }
        while(idx < size) {
            st.insert(arr[idx]);
            auto it = mp.find(arr[idx]);
            int i=(it->second).first, j=(it->second).second;
            bool valid1=true,valid2=true;

            for(int col=0;col<n;col++) {
                if(st.find(mat[i][col]) == st.end()) {
                     valid1=false;
                    break;
                }

            }

            for(int row=0;row<m;row++) {
                if(st.find(mat[row][j]) == st.end()) {
                    valid2=false;
                    break;
                }

            }
            if(valid2 || valid1)return idx;

                idx++;
        }
        return -1;
    }
};

