class Solution {
public:
    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& matrix) {
        int m= matrix.size();
        int n= matrix[0].size();
        int t= arr.size();
        int ans= m*n-1;

        unordered_map<int, int>mp;
        for(int i=0; i<t; i++) mp[arr[i]]=i; // store the index of arr element in map

        //row wise check 
        for(int i=0; i<m; i++) {
            int rowmax=0; //  maximum index required (have to traverse) to color that full row
            for(int j=0; j<n; j++) {
                rowmax= max(rowmax, mp[matrix[i][j]]);
            }
            ans= min(ans, rowmax); 
        }

        //column wise check
        for(int i=0; i<n; i++) {
            int colmax=0;
            for(int j=0; j<m; j++) {
                colmax= max(colmax, mp[matrix[j][i]]); //  maximum index required (have to traverse) to color that full col
            }
            ans= min(ans, colmax);
        }
        return ans;

    }
};
```

