function firstCompleteIndex(arr: number[], mat: number[][]): number {
  // HashMaps are the only data structure that can give us mat[x][y] in constant time
  const patterns = new Map<number, [number, number]>()

  const m = mat.length
  const n = mat[0].length

  // Step 1; Create the HashMap. tc: O(n*m), sc: O(n+m)
  for (let i = 0; i < m; i++) {
    for (let j = 0; j < n; j++) {
        patterns.set(mat[i][j], [i, j])
    }
  }


  // Step 2; Keep counts of the painted row and columns
  const paintedInRow: number[] = Array(m).fill(0)
  const paintedInCol: number[] = Array(n).fill(0)

  for (let i = 0; i < arr.length; i ++) { // tc: O(n+m), sc: O(1)
    // get row and col indexes
    const [x, y] = patterns.get(arr[i]) as [number, number]
    // increment
    paintedInRow[x]++;
    paintedInCol[y]++;
    // the loop stops if max(rows) = cols.length or max(cols) = rows.length
    // i.e. the counts and maxes are reversed.
    if(paintedInRow[x] === n || paintedInCol[y] === m) return i
  }
};


```

