class Solution {
public:
    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& mat) {
        int m = mat.size();
        int n = mat[0].size();
        unordered_map<int,pair<int, int>> umap_wall;
        vector<int> row_cell(m+n,0);

        for (int i = 0; i<m; i++){
            for (int j = 0; j<n; j++){
                umap_wall[mat[i][j]] = {i,j};
            }
        }

        for (int i = 0; i<arr.size(); i++){
            pair<int, int>& index = umap_wall[arr[i]];
            row_cell[index.first]++;
            row_cell[m+index.second]++;

            if (row_cell[index.first]==n) return i;
            if (row_cell[m+index.second]==m) return i;
        }
        return arr.size()-1;
    }
};
```

