class Solution {
public:
    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& mat) {
        int n = mat.size();
        int m = mat[0].size();
        vector<long long int> row;
        vector<long long int> col;
        for(int i = 0;i<n;i++){
            long long int sum = 0;
            for(int j = 0;j<m;j++){
                sum+=mat[i][j];
            }
            row.push_back(sum);
        }
        for(int i = 0;i<m;i++){
            long long int sum = 0;
            for(int j = 0;j<n;j++){
                sum+=mat[j][i];
            }
            col.push_back(sum);
        }
        unordered_map<int,pair<int,int>> map;
        for(int i = 0;i<n;i++){
            for(int j = 0;j<m;j++){
                map[mat[i][j]] = {i,j};
            }
        }
        for(int i = 0;i<arr.size();i++){
            pair<int,int> temp = map[arr[i]];
            int r = temp.first;
            int c = temp.second;
            row[r] = row[r]-arr[i];
            col[c] = col[c]-arr[i];
            if(row[r] == 0 || col[c] == 0) return i;

        }
        return 0;
    }
};
