function firstCompleteIndex(arr: number[], grid: number[][]): number {
    const ROW = grid.length;
    const COL = grid[0].length;

    const positionToPaintMap:Map<number, [number,number]> = new Map();


    let rowCounts: number[] = new Array(grid.length).fill(0)
    let colCounts: number[] = new Array(grid[0].length).fill(0)

    for(let x = 0; x < ROW;x++){
        for(let y = 0; y < COL; y++){
            positionToPaintMap.set(grid[x][y],[x,y])
        }
    }


    for(let i = 0; i < arr.length; i++){
        const position = positionToPaintMap.get(arr[i])
        if(++rowCounts[position[0]] == COL){
            return i
        }
        if(++colCounts[position[1]] == ROW){
            return i
        }
    }

    return -1
};
```

