class Solution {
public:
    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& mat) {
        int m = mat.size();
        int n = mat[0].size();

		//declaring map that stores the element in arr array as key and position m,n in the matrix mat as pair value
        unordered_map<int,pair<int,int>> mp;

		//frequency arrays for rows and columns initialized with 0
        vector<int> row(m,0);
        vector<int> col(n,0);

		//mapping values
        for(int i=0;i<mat.size();i++)
        {
            for(int j=0;j<mat[0].size();j++)
            {
              mp.insert({mat[i][j],{i,j}});
            }
        }

		//adding values in frequency arrays 
        for(int i=0;i<arr.size();i++)
        {
           row[mp.find(arr[i])->second.first]++;
           col[mp.find(arr[i])->second.second]++;
        }

		//finding and subtracting based on rows and columns of current element in arr
		//as soon as any number of row or column becomes 0 i.e it is fully painted we return index i as the answer
        for(int i=0;i<arr.size();i++)
        {
           int r = mp.find(arr[i])->second.first;
           int c = mp.find(arr[i])->second.second;
           row[r]--;
           col[c]--;
           if(row[r] == 0 || col[c] == 0) return i;
        }
        //return -1 just for the sake of returning value
        return -1;
    }
};
