class Solution {
    public int firstCompleteIndex(int[] arr, int[][] mat) {
        int n = arr.length;
        HashMap<Integer, Integer> map = new HashMap<>();

        int m = mat.length;
        int k = mat[0].length;
        for(int i =0;i<n;i++){
            map.put(arr[i], i);
        }
        int min = Integer.MAX_VALUE;

        for(int i =0;i<m;i++){
            int max = 0;
            for(int j =0;j<k;j++){
                max = Math.max(max, map.get(mat[i][j]));
            }
            min = Math.min(min, max);
        }

        for(int i =0;i<k;i++){
            int max = 0;
