class Solution {
public:

    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& mat) {
        int m = mat.size();
        int n = mat[0].size();
        // value -> coord map
        vector<pair<int, int>> mp(m*n+1, {-1,-1});

        // counters for each row and column.
        vector<int> row(m, 0);
        vector<int> col(n, 0);

        // value -> cord
        for(int r = 0; r < m; r++) {
            for(int c=0; c< n; c++) {
                mp[mat[r][c]] = {r, c};
            }
        }

        for(int i=0; i<arr.size(); i++) {
            auto& p = mp[arr[i]];
            row[p.first]++;
            col[p.second]++;
            if (row[p.first]==n || col[p.second]==m) {
                return i;
            }
        }
        return -1;
    }

};
```

