func firstCompleteIndex(arr []int, mat [][]int) int {
  m, n := len(mat), len(mat[0])
  // First we need to know which (row, col) each number refers to.
  positions := make([][2]int, len(arr)+1)
  for i := 0; i < m; i++ {
    for j := 0; j < n; j++ {
      positions[mat[i][j]] = [2]int{i, j}
    }
  }
  // rowPainted keeps track of the number of cells painted in each row
  rowPainted := make([]int, m)
  // colPainted keeps track of the number of cells painted in each column
  colPainted := make([]int, n)
  var res int
  for idx, id := range arr {
    i, j := positions[id][0], positions[id][1]
    rowPainted[i]++
    colPainted[j]++
    if rowPainted[i] == n || colPainted[j] == m {
      res = idx
      break
    }
  }
  return res
}
