class Pair{
    int row, col;
    Pair(int row, int col){
        this.row = row;
        this.col = col;
    }
}
class Solution {

    private static int paint(int[] arr, int[][] mat, Map<Integer,Pair> map,int[] rowFill,int[] colFill){
        int len = arr.length, Row = mat.length, Col = mat[0].length;
        for(int index=0;index<len;index++){
            int value = arr[index];
            Pair curPair = map.get(value);
            int row = curPair.row, col = curPair.col;
            rowFill[row]--;
            colFill[col]--;
            mat[row][col] = -1;
            if(rowFill[row]==0 || colFill[col]==0) return index;
        }
        return len-1;
    }

    private static int fun(int[] arr, int[][] mat){
        int len = arr.length, Row = mat.length, Col = mat[0].length;
        Map<Integer,Pair> map = new HashMap<>();
        for(int row=0;row<Row;row++){
            for(int col=0;col<Col;col++){
                map.put(mat[row][col], new Pair(row,col));
            }
        }
        int[] rowFill = new int[Row], colFill = new int[Col];
        Arrays.fill(rowFill,Col);
        Arrays.fill(colFill,Row);
        return paint(arr,mat,map,rowFill,colFill);
    }

    public int firstCompleteIndex(int[] arr, int[][] mat) {
        return fun(arr,mat);
    }
}

// Optimized Code 
// Time Complexity : O(Row*Col + Row*Col + len)
// Space Complexity : O(Row*Col + Row + Col)
