class Solution:
    def firstCompleteIndex(self, arr: List[int], mat: List[List[int]]) -> int:
        m, n = len(mat), len(mat[0])
        rows = [0] * m
        cols = [0] * n

        paint = defaultdict(lambda :(-1,-1))

        for r in range(m):
            for c in range(n):
                paint[mat[r][c]] = (r,c)

        for i in range(len(arr)):
            r,c = paint[arr[i]]

            rows[r] +=1
            cols[c] +=1


            if rows[r] == n or cols[c] == m:
                return i

        return m*n -1

