class Solution {
public:
    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& mat) {
        // const int size = arr.size();
        const int M = mat.size();
        if (M==0) return 0;
        const int N = mat[0].size();

        vector<int> rows(M,0);
        vector<int> cols(N,0);

        vector<tuple<int, int>> mat_proj(M*N, {0,0});

        for (int m=0; m<M; m++) {
            for (int n=0; n<N; n++) {
                int el = mat[m][n];
                mat_proj[el-1] = {m,n};
            }
        }

        int idx=0;
        for (const int a : arr) {
            tuple<int,int> tup = mat_proj[a-1];
            if (++rows[get<0>(tup)] == N || ++cols[get<1>(tup)] == M) return idx;
            idx++;
        }
        return idx;
    }
};
```

