object Solution {
  def firstCompleteIndex(arr: Array[Int], mat: Array[Array[Int]]): Int = {
    val (m, n) = (mat.size, mat.head.size)
    val (rows, cols) = (Array.fill(m)(n), Array.fill(n)(m))
    val ijs = mat.indices.flatMap(i => mat.head.indices.map(j => mat(i)(j) -> (i, j))).toMap
    arr.map(ijs).indexWhere { case (i, j) =>
      rows(i) = rows(i) - 1
      cols(j) = cols(j) - 1
      rows(i) == 0 || cols(j) == 0
    }
  }
}
