class Solution {
public:
    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& mat) {
        int n=mat.size(),m=mat[0].size();

        vector<pair<int,int>> pos(m*n+1);
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                pos[mat[i][j]]={i,j};
            }
        }

        for(int i=0;i<arr.size();i++){
            int x=pos[arr[i]].first,y=pos[arr[i]].second;
            mat[x][y]=-i;
        }

        vector<int> v;
        for(int i=0;i<n;i++){
            int mini=INT_MAX;
            for(int j=0;j<m;j++){
                mini=min(mini,mat[i][j]);
