        for(int i=0;i<m;i++){
            int mini=INT_MAX;
            for(int j=0;j<n;j++){
                mini=min(mini,mat[j][i]);
            }
            v.push_back(mini);
        }

        sort(v.rbegin(),v.rend());

        return -v[0];
    }
};
