 * @param {number[][]} mat
 * @return {number}
 */
const firstCompleteIndex = function(arr, mat) {
  const m = mat.length;
  const n = mat[0].length;

  // create an array to store the coordinates of all elements in the matrix
  const coordinates = Array(m * n + 1);
  for (let i = 0; i < m; i++) {
    for (let j = 0; j < n; j++) {
      coordinates[mat[i][j]] = [i, j];
    }
  }

  // create two arrays to store the remaining count of elements in each row and column
  const rowCount = Array(m).fill(n);
  const colCount = Array(n).fill(m);

  // iterate through the array of numbers and update the row and column counts
  for (let i = 0; i < m * n; i++) {
    const [row, col] = coordinates[arr[i]];
    if (--rowCount[row] === 0 || --colCount[col] === 0) {
      // if any row or column has no remaining elements, return the current index
      return i;
    }
  }
};
