class Solution {
    func firstCompleteIndex(_ arr: [Int], _ mat: [[Int]]) -> Int {
        let m = mat.count
        let n = mat[0].count

        var rows = Array(repeating: n, count: m)
        var cols = Array(repeating: m, count: n)

        var map = [Int: [Int]]()
        for r in 0..<m {
            for c in 0..<n {
                map[mat[r][c]] = [r,c]
            }
        }

        for i in 0..<m*n {
            if let pairs = map[arr[i]] {
                let r = pairs[0]
                let c = pairs[1]
                rows[r] -= 1
                if rows[r] == 0 { return i }
                cols[c] -= 1
                if cols[c] == 0 { return i }
            }
        }
        return m*n-1
    }
}
