class Solution {
    public int firstCompleteIndex(int[] arr, int[][] mat) {
        int n = mat.length;
        int m = mat[0].length;
        int len = n * m;
        final int RSUM = m;
        final int CSUM = n;
        int[][] cor = new int[len+1][2];

        int[] rowSum = new int[n];
        int[] colSum = new int[m];


        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                int val = mat[i][j];
                cor[val] = new int[]{i,j};
            }
        }

        for(int i=0;i<len;i++){
            int val = arr[i];
            int r = cor[val][0];
            int c = cor[val][1];

            rowSum[r]++;
            colSum[c]++;

            if(rowSum[r] >= RSUM || colSum[c] >= CSUM)
                return i;
        }
        return len-1;
    }
}
