class Solution {
    public int firstCompleteIndex(int[] arr, int[][] mat) {
        int rows = mat.length, cols = mat[0].length;
        int[] coloredRows = new int[rows];
        int[] coloredColumns = new int[cols];
        int[] matPositions = getMatPositions(mat);
        int rowToPaint, colToPaint;
        for (int i = 0; i < arr.length; i++) {
            rowToPaint = matPositions[arr[i]] / cols;
            colToPaint = matPositions[arr[i]] % cols;
            if (++coloredRows[rowToPaint] == cols || ++coloredColumns[colToPaint] == rows) {
                return i;
            }
        }
        return -1;
    }

    private int[] getMatPositions(int[][] mat) {
        int[] positions = new int[mat.length * mat[0].length + 1];
        int pos = 0;
        for (int[] row : mat) {
            for (int num : row) {
                positions[num] = pos++;      
            }
        }
        return positions;
    }
}
```

