var firstCompleteIndex = function(arr, mat) {
    const n = mat.length;
    const m = mat[0].length;
    let map = new Map();
    for(let i = 0; i < n; i++){
        for(let j = 0; j < m; j++){
            map.set(mat[i][j], [i, j])
        }
