class Solution {
public:
    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& mat) {
        int n = mat.size(), m = mat[0].size();
        map<int, pair<int, int>> mp;
        vector<int> r(n, 0), c(m, 0); //Frequency Array for checking completely painted row or column

        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                mp[mat[i][j]] = {i, j}; //Mapping row and column value to the element
            }
        }

        int ans = 0;
        for(int i=0; i<arr.size();i++){
            r[mp[arr[i]].first]++;
            c[mp[arr[i]].second]++;

            if(r[mp[arr[i]].first] == m || c[mp[arr[i]].second] == n){
                ans = i;
                break;
            }
        }

        return ans;
    }
};
