class Solution {
    struct helper{
        int row;
        int col;
    };
public:
    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& mat) {
        const int M = mat.size();
        const int N = mat[0].size();

        vector<int> rows(M, N);
        vector<int> columns(N, M);

        unordered_map<int, helper> lookup;
        for(int r = 0; r < M; ++r) {
            for(int c = 0; c < N; ++c) {
                lookup[mat[r][c]] = {r,c};
            }
        }

        for(int i = 0; i < M * N; ++i) {
            const auto &coord = lookup[arr[i]];
            if(--rows[coord.row] == 0 || --columns[coord.col] == 0) {
                return i;
            }
        }
        return M * N;
    }
};
