class Solution {
    public int firstCompleteIndex(int[] arr, int[][] mat) {
        int m = mat.length, n = mat[0].length;
        int[] rowCount = new int[m];
        int[] colCount = new int[n];
        var matLookup = new HashMap<Integer, int[]>();//number vs (mat -> row,col)

        //let's fill the map
        for(int row = 0; row < m; row++){
            for(int col = 0; col < n; col++){
                matLookup.put(mat[row][col], new int[]{row, col});
            }
        }

        //let's iterate over the num array and find out its occurance in the mat 
        for(int i = 0; i < arr.length; i++){
            //fetch the position .i.e row and col pos in the mat for the curr num
            int currNum = arr[i];
            int rowIdx = matLookup.get(currNum)[0];
            int colIdx = matLookup.get(currNum)[1];
            //since we know we have to paint the cell
            //we also upadate the number of painted rows and cols at that specific row and col 
            rowCount[rowIdx]++;
            colCount[colIdx]++;
            //now let's try to see if at this time we have a fully painted row or a col
            if(rowCount[rowIdx] == n || colCount[colIdx] == m)
                return i;
        }

        return -1;

    }
}

