    bool check_row(pair<int, int> loc, vector<vector<int>>& mat){
        int flag = 1;
        for(int j=0; j < mat[0].size(); j++){
            if(mat[loc.first][j]!=-1){
                flag = 0;
                break;
            } 
        } 
        return flag;
    }

    bool check_column(pair<int, int> loc, vector<vector<int>>& mat){
        int flag = 1;
        for(int i=0; i < mat.size(); i++){
            if(mat[i][loc.second]!=-1){
                flag = 0;
                break;
            }
        }
        return flag;
    }

