        int count =0;
        for(int i=0; i < arr.size(); i++){
            loc = mp[arr[i]];
            mat[loc.first][loc.second] = -1;
            count++;
            if(count >= mat[0].size()){
                if(check_row(loc, mat)) return i;
            }
            if(count >= mat.size()){
                if(check_column(loc, mat)) return i;
            }

        }
        return -1;
    }
};
