public class Solution {
    public int FirstCompleteIndex(int[] arr, int[][] mat) {
        var dic = new Dictionary<int, (int, int)>();
        for (var i = 0; i < mat.Length; i++) {
            for (var j = 0; j < mat[i].Length; j++) {
                dic.Add(mat[i][j], (i, j));
            }
        }

        var row = new int[mat.Length];
        var col = new int[mat[0].Length];
        var rowIndex = 0;
        var colIndex = 0;
        for (var k = 0; k < arr.Length; k++) {
            rowIndex = dic[arr[k]].Item1;
            colIndex = dic[arr[k]].Item2;
            row[rowIndex]++;
            col[colIndex]++;
            if (row[rowIndex] == mat[0].Length || col[colIndex] == mat.Length)
                return k;
        }

        return 0;   
    }
}
