``` java []
class Solution {
    private static int dist(int x1, int y1, int x2, int y2) {
        return Math.abs(x1 - x2) + Math.abs(y1 - y2);
    }

    public int minimumCost(int[] start, int[] target, int[][] specialRoads) {
        int n = specialRoads.length;
        var dist = new int[n];
        int minDistIdx = 0;

        for (int i = 0; i < n; i++) {
            dist[i] = dist(
                    start[0], start[1],
                    specialRoads[i][0], specialRoads[i][1]
            ) + specialRoads[i][4];
            if (dist[minDistIdx] > dist[i]) minDistIdx = i;
        }

        int cost = dist(start[0], start[1], target[0], target[1]);
        while (minDistIdx != -1) {
            var cur = minDistIdx;
            minDistIdx = -1;

            cost = Math.min(cost, dist[cur] + dist(
                    target[0], target[1],
                    specialRoads[cur][2], specialRoads[cur][3]
            ));

            for (int i = 0; i < n; i++) {
                if (i == cur || dist[i] == -1) continue;

                dist[i] = Math.min(dist[i], dist[cur] + dist(
                        specialRoads[cur][2], specialRoads[cur][3],
                        specialRoads[i][0], specialRoads[i][1]
                ) + specialRoads[i][4]);

                if (minDistIdx == -1 || dist[minDistIdx] > dist[i]) {
                    minDistIdx = i;
                }
            }

            dist[cur] = -1;
        }

        return cost;
    }
}
