class Solution {
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        int n = specialRoads.size();
        int sx = start[0], sy = start[1], ex = target[0], ey = target[1];
        int ans = abs(sx - ex) + abs(sy - ey);
        map<pair<int, int>, int> dist;
        set<array<int, 3>> pq; pq.insert({0, sx, sy});
        dist[{sx, sy}] = 0;
        while (!pq.empty()){
            auto [cost, x, y] = *pq.begin(); pq.erase(pq.begin());
            ans = min(ans, cost + abs(x - ex) + abs(y - ey));
            if (ans < cost) break;
            for (auto &e : specialRoads){
                int fx = e[0], fy = e[1], tx = e[2], ty = e[3], c = e[4];
                int ncost = cost + min(abs(fx - x) + abs(fy - y) + c, abs(x - tx) + abs(y - ty));
                int pcost = (dist.count({tx, ty}) ? dist[{tx, ty}] : INT_MAX);
                if (pcost > ncost){
                    if (pq.count({pcost, tx, ty})) pq.erase({pcost, tx, ty});
                    dist[{tx, ty}] = ncost;
                    pq.insert({ncost, tx, ty});
                }
            }
        };
        return ans;
    }
};
