class Solution {
private:
    int dijkstra(vector<int>& s, vector<int>& t,map<pair<int,int>,set<pair<pair<int,int>,int>>>&g){
        set<pair<int,pair<int,int>>> pq;
        pq.insert({0,{s[0],s[1]}});
        map<pair<int,int>,int> dist;
        dist[{s[0],s[1]}] = 0;
        while(!pq.empty()){
            auto top = *pq.begin();
            pq.erase(pq.begin());
            int wt = top.first;
            pair<int,int> par = top.second;
            for(auto&[child,weight]: g[par]){
                int newwt = dist[par] + weight;
                if(!dist.count(child)) dist[child] = 1e9;
                if(dist[child] > dist[par] + weight){
                    dist[child] = newwt;
                    pq.insert({newwt,child});
                }
            }
        }
        return dist[{t[0],t[1]}];
    }
    map<pair<int,int>,set<pair<pair<int,int>,int>>> buildGraph(vector<vector<int>>& sr){
        int n = size(sr);
        map<pair<int,int>,set<pair<pair<int,int>,int>>> g;
        for(int i=0; i<n; ++i){
            int a = sr[i][0], b = sr[i][1], c = sr[i][2], d = sr[i][3], wt = sr[i][4];
            g[{a,b}].insert({{c,d},wt});
            int wt2 = abs(a-c) + abs(b-d);
            if(wt2) g[{c,d}].insert({{a,b},wt2});
            for(int j=i+1; j<n; ++j){
                int w = sr[j][0], x = sr[j][1], y = sr[j][2], z = sr[j][3];
                int wt3 = abs(a-w) + abs(b-x);
                int wt4 = abs(a-y) + abs(b-z);
                if(wt3) g[{a,b}].insert({{w,x},wt3});
                if(wt4) g[{a,b}].insert({{y,z},wt4});
                if(wt3) g[{w,x}].insert({{a,b},wt3});
                if(wt4) g[{y,z}].insert({{a,b},wt4});

                int wt5 = abs(c-y) + abs(d-z);
                int wt6 = abs(c-w) + abs(d-x);
                if(wt6) g[{c,d}].insert({{w,x},wt6});
                if(wt5) g[{c,d}].insert({{y,z},wt5});
                if(wt6) g[{w,x}].insert({{c,d},wt6});
                if(wt5) g[{y,z}].insert({{c,d},wt5});
            }
        }
        return g;
    }
public:
    int minimumCost(vector<int>& s, vector<int>& t, vector<vector<int>>& sr) {
        int dist = abs(t[0]-s[0]) + abs(t[1] - s[1]);
        sr.push_back({s[0],s[1],t[0],t[1],dist});
        map<pair<int,int>,set<pair<pair<int,int>,int>>> graph = buildGraph(sr);
        return dijkstra(s,t,graph);
    }
};
