```
class Solution {
public:
    //observation: the minimum cost must be lower than the default manhattan distance 
    //hence, we must use a combination of the special roads to get to the target
    //keep jumping from special roads to other special roads, stop if we are above the minimum cost 
    struct Dis{
        int x;
        int y;
        int cost; 
    };

    int mtn(int x1, int y1, int x2, int y2){
        return abs(x2 - x1) + abs(y2 - y1); 
    }

    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        int x_t = target[0];
        int y_t = target[1]; 
        int base_dis = mtn(start[0], start[1], target[0], target[1]);
        queue<Dis> specs; 
        vector<int> min_costs(specialRoads.size(), INT_MAX); 
        for (const auto& road: specialRoads){
            specs.push({road[0], road[1], mtn(start[0], start[1], road[0], road[1])});
        }
        while (!specs.empty()){
            Dis curr = specs.front();
            specs.pop();
            base_dis = min(base_dis, curr.cost + mtn(curr.x, curr.y, x_t, y_t));
            for (int i = 0; i < specialRoads.size(); ++i){
                int next_cost = mtn(curr.x, curr.y, specialRoads[i][0], specialRoads[i][1]);
                if (curr.cost + next_cost + specialRoads[i][4] >= base_dis){
                    continue; 
                }
                if (curr.cost + next_cost >= min_costs[i]){
                    continue; 
                }
                min_costs[i] = curr.cost + next_cost; 
                specs.push({specialRoads[i][2], specialRoads[i][3], curr.cost + next_cost + specialRoads[i][4]});
            }
            // for (const auto& road: specialRoads){ 
            //     int next_cost = mtn(curr.x, curr.y, road[0], road[1]);
            //     if (curr.cost + next_cost + road[4] >= base_dis){
            //         continue; 
            //     }
            //     if (curr.cost + next_cost )
            //     specs.push({road[2], road[3], curr.cost + next_cost + road[4]});
            // }
        }
        return base_dis; 

    }
};
