class Solution {
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        int startX = start[0] , startY = start[1];
        int targetX = target[0] , targetY = target[1];
        if(startX == targetX && startY == targetY) return 0;

        map<pair<int,int>,int> cost; /* {{X:CoOrdinate,Y:CoOrdinate},minCostToXY} */

        cost[{startX,startY}] = 0; /* Starting Co-Ordinstes */
        cost[{targetX,targetY}] = 1e9 + 8621; /* Target Co-ordinates : Initially Unreachable */

        /* priority Queue (minHeap) -> {CostToReachXY,{X,Y}} */
        priority_queue<pair<int,pair<int,int>>,vector<pair<int,pair<int,int>>>,greater<pair<int,pair<int,int>>>> pq;
        pair<int,pair<int,int>> tuple = {0,{startX,startY}};
        pq.push(tuple);

        while(!pq.empty()){
            /* Always Get The Minimum Cost Path */
            tuple = pq.top(); pq.pop();
            int currX = tuple.second.first;
            int currY = tuple.second.second;
            int currCost = tuple.first;

            /* If Reached Target */
            if(currX == targetX && currY == targetY) return currCost;

            /* Directly to target */
            if(cost[{targetX,targetY}] > currCost + abs(currX-targetX)+abs(currY-targetY)){
                /* Total Cost = Current Cost + |X1-X2| + |Y1-Y2| */
                cost[{targetX,targetY}] = currCost + abs(currX-targetX) + abs(currY-targetY);
                pq.push({cost[{targetX,targetY}],{targetX,targetY}});
            }

            /* Via Special Road */
            for(vector<int> &road : specialRoads){ 
                /* Total Cost = Current Cost + Cost To Reach Special Road + Cost To Cross The Special Road */
                int toReachCost = currCost + abs(currX - road[0]) + abs(currY - road[1]) + road[4];
                if(cost.find({road[2],road[3]}) == cost.end() || cost[{road[2],road[3]}] > toReachCost){
                    cost[{road[2],road[3]}] = toReachCost;
                    pq.push({toReachCost,{road[2],road[3]}});
                }
            }
        }
        return -1;
    }
};

