class Solution {
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& s) {

        // pushing special roads 2 time to consider backward movement also from any road.
        vector<vector<int>> sr(s.begin(), s.end());
        for(int i = 0; i < s.size(); ++i)
            sr.push_back(s[i]);
        int n = sr.size();
        vector<int> dp(n, INT_MAX);
        // dp[i] = min distance to go from i to target
        dp[n-1] = abs(target[0]-sr[n-1][2])+abs(target[1]-sr[n-1][3])+ sr[n-1][4];
        for(int i = n-2; i >= 0; --i) {
            for(int j = i+1; j < n; ++j) {
                int temp = sr[i][4] + abs(sr[j][0]-sr[i][2])+abs(sr[j][1]-sr[i][3])+dp[j];
                dp[i] = min(dp[i], temp);
            }
            dp[i] = min(dp[i], (sr[i][4]+abs(sr[i][2]-target[0])+abs(sr[i][3]-target[1])));
        }
        int mini = abs(start[0]-target[0])+abs(start[1]-target[1]);
        for(int i = 0; i < n/2; ++i) {
            int temp = abs(start[0]-sr[i][0])+abs(start[1]-sr[i][1])+dp[i];
            mini = min(mini, temp);
        }
        return mini;
    }
};

