        return this.getParentIdx(idx) >= 0
    }

    getLeftChildIdx(idx) {
        return idx * 2 + 1
    }
    getRightChildIdx(idx) {
        return idx * 2 + 2
    }
    getParentIdx(idx) {
        return Math.trunc((idx - 1) / 2);
    }

    getLeftChild(idx) {
        return this.heap[getLeftChildIdx(idx)]
    }
    getRightChild(idx) {
        return this.heap[getRightChildIdx(idx)]

    }
    getParent(idx) {
        return this.heap[getParentIdx(idx)]

    }

    compare(first, second) {
        return this.predicate(this.heap[first], this.heap[second])
    }

    swap(idx1, idx2) {
        const temp = this.heap[idx1];
        this.heap[idx1] = this.heap[idx2]
        this.heap[idx2] = temp
    }

}
