from queue import PriorityQueue
from collections import defaultdict
from typing import List

class Solution:
    def minimumCost(self, start: List[int], target: List[int], specialRoads: List[List[int]]) -> int:

        # Create the graph
        adj = defaultdict(list)
        for i, j, x, y, w in specialRoads:
            adj[(i,j)].append(((x,y), w))

        for i in range(start[0], target[0]+1):
            for j in range(start[1], target[1]+1):
                for x, y in ((-1, 0), (1, 0), (0, -1), (0, 1)):
                    r, c = i+x, j+y
                    if r >= start[0] and r <= target[0] and c >= start[1] and c <= target[1]:
                        adj[(i,j)].append(((r, c), abs(i-r)+abs(j-c)))

        # Run Dijkstra's algorithm
        pq = PriorityQueue()
        pq.put((0, tuple(start)))
        dist = {tuple(start): 0}

        while not pq.empty():
            d, u = pq.get()
            if u == tuple(target):
                return d
            for v, w in adj[u]:
                alt = d + w
                if v not in dist:
                    dist[v] = float('inf')
                if alt < dist[v]:
                    dist[v] = alt
                    pq.put((alt, v))


        return -1  # target not reachable

