class Solution {
    using ipii = pair<int, pair<int, int>>;
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {

        map<pair<int, int>, int> spr; //    storing min distances
        spr[{start[0], start[1]}] = 0; //   src node
        vector<vector<int>> roads; //   storing only valid specialRoads
        for(auto i : specialRoads) {
            int a = i[0], b = i[1], c = i[2], d = i[3], cost = i[4];

            if(cost < abs(a - c) + abs(b - d)) { //If valid then only it counts
                roads.push_back({a, b, c, d, cost});
                spr[{c, d}] = abs(c - start[0]) + abs(d - start[1]);
            }
        }
        priority_queue<ipii, vector<ipii>, greater<ipii>> pq; 
        //  ipii defined above

        pq.push({0, {start[0], start[1]}}); //Src node pushed

        while(!pq.empty()) {
            auto curr = pq.top(); pq.pop();
            int dist = curr.first, x = curr.second.first, y = curr.second.second;

            for(auto &i : roads) {
                int a = i[0], b = i[1], c = i[2], d = i[3], cost = i[4];
                //  If the already present distance is larger then update
                if(spr[{c, d}] > dist + abs(a - x) + abs(b - y) + cost) {
                    spr[{c, d}] = dist + abs(a - x) + abs(b - y) + cost;
                    pq.push({spr[{c, d}], {c, d}});
                }
            }
        }

        int tx = target[0], ty = target[1];
        int ans = abs(tx - start[0]) + abs(ty - start[1]);

        //  Calculation of the min distance (this works because we have the min distances till {i[2], i[3]} )
        // and we just need to find min distance from that
        for(auto &i : roads) {
            int c1 = abs(tx - i[2]), d1 = abs(ty - i[3]);
            ans = min(ans, c1 + d1 + spr[{i[2], i[3]}]);
        }

        return ans;
    }
};
