class Solution {
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads)
    {
        int ans = abs(target[1]-start[1])+abs(target[0]-start[0]);
        vector<vector<int>> req;
        for(vector<int>&sr: specialRoads)
        {
            if(abs(sr[3]-sr[1])+abs(sr[2]-sr[0])>sr[4]){req.push_back(sr);}
        }
        int m = req.size();
        if(m==0){return ans;}
        sort(req.begin(), req.end());
        vector<int> dis(m);
        for(int i=0;i<m;i++){dis[i]=abs(req[i][0]-start[0])+abs(req[i][1]-start[1]);}
        int a1 = dis[0];int a2 = 0;
        for(int i=1;i<m;i++){if(dis[i]<a1){a1=dis[i];a2=i;}}
        vector<bool> comp(m, false);
        queue<int> q;
        q.push(a2);
        while(q.empty()==false)
        {
            int b = q.front();
            q.pop();
            comp[b]=true;
            int b1 = INT_MAX;int b2 = -1;
            for(int i=0;i<m;i++)
            {
                if(comp[i]==false)
                {
                    int c = dis[b]+req[b][4]+abs(req[i][0]-req[b][2])+abs(req[i][1]-req[b][3]);
                    if(dis[i]>c)
                    {
                        dis[i]=c;
                    }
                    if(dis[i]<b1){b1=dis[i];b2=i;}
                }
            }
            if(b2!=-1){q.push(b2);}
        }

        vector<int> dit(m);
        for(int i=0;i<m;i++){dit[i]=abs(req[i][2]-target[0])+abs(req[i][3]-target[1]);}
        int a3 = dit[0];int a4 = 0;
        for(int i=1;i<m;i++){if(dit[i]<a3){a3=dit[i];a4=i;}}
        vector<bool> comq(m, false);
        queue<int> qt;
        qt.push(a4);
        while(qt.empty()==false)
        {
            int b = qt.front();
            qt.pop();
            comq[b]=true;
            int b1 = INT_MAX;int b2 = -1;
            for(int i=0;i<m;i++)
            {
                if(comq[i]==false)
                {
                    int c = dit[b]+req[b][4]+abs(req[i][2]-req[b][0])+abs(req[i][3]-req[b][1]);
                    if(dit[i]>c)
                    {
                        dit[i]=c;
                    }
                    if(dit[i]<b1){b1=dis[i];b2=i;}
                }
            }
            if(b2!=-1){qt.push(b2);}
        }

        for(int i=0;i<m;i++){ans=min(ans, dis[i]+dit[i]+req[i][4]);}
        return ans;
    }
};

