class Solution {
public:
    using pii=pair<int, int>;
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& sr) {
        unordered_map<string,int>m;
        int count=1;
        string key_s=to_string(start[0])+" "+to_string(start[1]);
        m[key_s]=count;
        count++;
        string key_t=to_string(target[0])+" "+to_string(target[1]);
        m[key_t]=count;
        count++;
        vector<vector<int>>t=sr;
        sr.clear();
        for(int i=0;i<t.size();i++){
            string key=to_string(t[i][0])+" "+to_string(t[i][1]);
            if(!m.count(key)){
                m[key]=count;
                count++;
            }
            key=to_string(t[i][2])+" "+to_string(t[i][3]);
            if(!m.count(key)){
                m[key]=count;
                count++;
            }
        }
        vector<vector<pair<int,int>>>v(count+1);
        v[1].push_back({2,abs(target[0]-start[0])+abs(target[1]-start[1])});
        for(int i=0;i<t.size();i++){
            int n1=m[to_string(t[i][0])+" "+to_string(t[i][1])];
            int n2=m[to_string(t[i][2])+" "+to_string(t[i][3])];
            v[1].push_back({n1,abs(t[i][0]-start[0])+abs(t[i][1]-start[1])});
            v[n2].push_back({2,abs(t[i][2]-target[0])+abs(t[i][3]-target[1])});
            v[n1].push_back({n2,t[i][4]});
            for(int j=0;j<t.size();j++){
                if(j!=i){
                    int m1=m[to_string(t[j][0])+" "+to_string(t[j][1])];
                    v[n2].push_back({m1,abs(t[i][2]-t[j][0])+abs(t[i][3]-t[j][1])});
                }
