class Solution {
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        int sx = start[0];
        int sy = start[1];
        int ex = target[0];
        int ey = target[1];
        int len = specialRoads.size();

        //pair<distance, special raod position>
        priority_queue<pair<int,int>, vector<pair<int,int>>, greater<pair<int,int>>> pq;

        for(int i=0; i<len; i++) {
            pq.push({abs(sx-specialRoads[i][0]) + abs(sy-specialRoads[i][1]), i});
        }

        vector<int> dp(len, INT_MAX);
        while(!pq.empty()) {
            auto[cost, i] = pq.top();
            pq.pop();

            if(dp[i] != INT_MAX) continue;

            dp[i] = cost+specialRoads[i][4];

            for(int j=0; j<len; j++) {
                if(i==j) continue;
                pq.push({dp[i]+abs(specialRoads[i][2]-specialRoads[j][0])+abs(specialRoads[i][3]-specialRoads[j][1]), j}); 
            }
        }

        int minCost = abs(sx-ex) + abs(sy-ey);
        for(int i=0; i<len; i++) {
            minCost = min(minCost, dp[i]+abs(specialRoads[i][2]-ex)+abs(specialRoads[i][3]-ey));
        }

        return minCost;
    }
};
