# Intuition
Connected Points with Manhattan dist and Special Road dist as applicable. Then used Dijkstra.

# Complexity
- Time complexity:
O(n^2 logn )

- Space complexity:
O(N)

# Code
```
class Solution {
public:
    int dist(vector<int> start,vector<int> end){
        return abs(start[0]-end[0])+abs(start[1]-end[1]);
    }
    void makeEdge(map<int,map<int,int>> &adj,int u,int v,int wt,bool uni){
        if(u==v){
            return;
        }        
        if(adj.count(u)==1){
            if(adj[u].count(v)==1){                
                adj[u][v]=min(adj[u][v],wt);
                if(!uni){                    
                    if(adj[v].count(u)==1){
                    adj[v][u]=min(wt,adj[v][u]);
                    }else{
                        adj[v][u]=wt;
                    }                                        
                }                  
                return;
            }
        }
        adj[u][v]=wt;
        if(!uni)
        {
            if(adj[v].count(u)==1){
                adj[v][u]=min(wt,adj[v][u]);
            }else{
                adj[v][u]=wt;
            } 
        }                            
    }
    void CrdToNode(map<int,map<int,int>> &mp,int x,int y,int &k){
        if(mp.count(x)==1){
            if(mp[x].count(y)==1){
                return;
            }
        }
        mp[x][y]=k;
        k+=1;
        return;
    }
    class Comp{
        public:
        bool operator()(pair<int,int> &a,pair<int,int> &b){
            return a.second<b.second;
        }
    };
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        int n=specialRoads.size();
        map<int,map<int,int>> Crds;
        int k=0;
        CrdToNode(Crds,start[0],start[1],k);
        CrdToNode(Crds,target[0],target[1],k);
        for(auto v:specialRoads){
            vector<int> st{v[0],v[1]};vector<int> end{v[2],v[3]};
            CrdToNode(Crds,st[0],st[1],k);
            CrdToNode(Crds,end[0],end[1],k);
        }        
        map<int,map<int,int>> adj;        
        int src=Crds[start[0]][start[1]];
        int dest=Crds[target[0]][target[1]];        
        makeEdge(adj,src,dest,dist(start,target),false);        
        for(int i=0;i<n;i++){
            vector<int> st{specialRoads[i][0],specialRoads[i][1]};vector<int> end{specialRoads[i][2],specialRoads[i][3]};
            int u1=Crds[st[0]][st[1]];int v1=Crds[end[0]][end[1]];            
            for(int j=i+1;j<n;j++){
                vector<int> s{specialRoads[j][0],specialRoads[j][1]};vector<int> e{specialRoads[j][2],specialRoads[j][3]};
                int u2=Crds[s[0]][s[1]];int v2=Crds[e[0]][e[1]]; 
                makeEdge(adj,u1,u2,dist(st,s),false);
                makeEdge(adj,u1,v2,dist(st,e),false);
                makeEdge(adj,v1,u2,dist(end,s),false);
                makeEdge(adj,v1,v2,dist(end,e),false);
            }
        }                
        for(auto vec:specialRoads){
            vector<int> st{vec[0],vec[1]};vector<int> end{vec[2],vec[3]};
            int u=Crds[st[0]][st[1]];
            int v=Crds[end[0]][end[1]];            
            makeEdge(adj,u,v,vec[4],true);            
            makeEdge(adj,src,u,dist(start,st),false);
            makeEdge(adj,dest,u,dist(target,st),false);
            makeEdge(adj,src,v,dist(start,end),false);
            makeEdge(adj,dest,v,dist(target,end),false);
        }        
        vector<int> dist(k,INT_MAX);
        vector<int> par(k);
        map<int,map<int,int>> edgeDist;
        for(int i=0;i<k;i++)par[i]=i;
        dist[src]=0;
        priority_queue<pair<int,int>,vector<pair<int,int>>,Comp> pq;
        pq.push(make_pair(src,0));
        while(!pq.empty()){
            pair<int,int> top=pq.top();
            pq.pop();
            int u=top.first;
            for(auto it:adj[u]){
                int v=it.first;
                int wt=it.second;                
                if(dist[u]+wt<dist[v]){
                    par[v]=u;
                    edgeDist[u][v]=wt;
                    dist[v]=dist[u]+wt;
                    pq.push(make_pair(v,dist[v]));
                }
            }
        }
        int u=dest;        
        return dist[dest];
    }
};
