        dist[{start[0],start[1]}]=0;
        dist[{target[0],target[1]}]=1e9;

        m[{start[0],start[1]}].push_back({target[0],target[1],
                abs(target[0]-start[0])+abs(target[1]-start[1])}); //start to target
        for(auto road:sr){
            dist[{road[0],road[1]}]=1e9;
            dist[{road[2],road[3]}]=1e9;

            m[{start[0],start[1]}].push_back({road[0],road[1],abs(road[0]-start[0])+abs(road[1]-start[1])});
            //start to road begin

            m[{road[0],road[1]}].push_back({road[2],road[3],road[4]});
            //road start to end

            //road end to target
            m[{road[2],road[3]}].push_back({target[0],target[1],
                    abs(target[0]-road[2])+abs(target[1]-road[3])});

            //road ends to other road start
            for(auto road1:sr){
                if(road!=road1){
                    m[{road[2],road[3]}].push_back({road1[0],road1[1], 
                    abs(road1[0]-road[2])+abs(road1[1]-road[3])});
                }
            }
        }

