class Solution {
    class Pair{
        int x;
        int y;
        int cost;
        Pair(int x,int y,int cost){
            this.x=x;
            this.y=y;
            this.cost=cost;
        }
    }

    public int minimumCost(int[] start, int[] target, int[][] sr) {
        int m = sr.length;
        int n = sr[0].length;

        HashMap<Integer, HashSet<Integer>> map = new HashMap<>();

        PriorityQueue<Pair> pq = new PriorityQueue<>((a, b) -> a.cost - b.cost);

        int fx = target[0];
        int fy = target[1];

        pq.add(new Pair(start[0], start[1], 0));

        while(!pq.isEmpty()){
            Pair curr = pq.remove();

            int x1 = curr.x;
            int y1 = curr.y;

            if(map.containsKey(x1) && map.get(x1).contains(y1)){
                continue;
            }

            if(map.containsKey(x1)==false){
                map.put(x1,new HashSet<Integer>());
            }
            map.get(x1).add(y1);

            if(curr.x == fx && curr.y == fy){
                return curr.cost;
            }

            int d = curr.cost + Math.abs(x1 - fx) + Math.abs(y1 - fy);

            pq.add(new Pair(fx, fy, d));

            for(int i = 0; i < sr.length; i++){
                int x=sr[i][0];
                int y=sr[i][1];
                int x2=sr[i][2];
                int y2=sr[i][3];
                int ct=sr[i][4];


                if(map.containsKey(x2)&&map.get(x2).contains(y2)){
                    continue;
                }
                pq.add(new Pair(x2,y2,curr.cost+ct+Math.abs(x1-x)+Math.abs(y1-y)));
            }



        }


        return -1;
    }
}






