solutions.content:
Treat potisions are nodes and roads as edges. We can also create an edge from any position to any other position with the cost of `|x2 - x1| + |y2 - y1|`. Then run Dijkstra's algorithm on this graph.

# Code
```
type PriorityQueue [][3]int // pair<x, y, cost>

func (pq PriorityQueue) Len() int           { return len(pq) }
func (pq PriorityQueue) Less(i, j int) bool { return pq[i][2] < pq[j][2] }
func (pq PriorityQueue) Swap(i, j int)      { pq[i], pq[j] = pq[j], pq[i] }

func (pq *PriorityQueue) Push(x interface{}) {
  item := x.([3]int)
  *pq = append(*pq, item)
}

func (pq *PriorityQueue) Pop() interface{} {
  old := *pq
  n := len(old)
  item := old[n-1]
  *pq = old[:n-1]
  return item
}

func minimumCost(start []int, target []int, specialRoads [][]int) int {
  startXY := [2]int{start[0], start[1]}
  targetXY := [2]int{target[0], target[1]}
  adjacencyList := make(map[[2]int][][3]int)
  adjacencyList[startXY] = append(adjacencyList[startXY],
    [3]int{target[0], target[1], dist(start[0], start[1], target[0], target[1])})

  for i := 0; i < len(specialRoads); i++ {
    road := specialRoads[i]
    x1,  y1, x2, y2, cost := road[0], road[1], road[2], road[3], road[4]
    adjacencyList[[2]int{x1,y1}] = append(adjacencyList[[2]int{x1,y1}], [3]int{x2,y2,cost})
    // start -> x1, y1
    adjacencyList[startXY] = append(adjacencyList[startXY], [3]int{x1, y1, dist(start[0], start[1], x1, y1)})
    // x2, y2 -> target
    adjacencyList[[2]int{x2, y2}] = append(adjacencyList[[2]int{x2, y2}], [3]int{target[0], target[1], dist(x2, y2, target[0], target[1])})
    // End of a special Road to all other special road starts
    for j := 0; j < len(specialRoads); j++ {
      if i == j {
        continue
      }
      x3, y3 := specialRoads[j][0], specialRoads[j][1]
      adjacencyList[[2]int{x2, y2}] = append(adjacencyList[[2]int{x2, y2}], [3]int{x3, y3, dist(x2, y2, x3, y3)})
    }
  }
  poscost := make(map[[2]int]int)
  poscost[startXY] = 0
  var pq PriorityQueue
  heap.Push(&pq, [3]int{start[0], start[1], 0}) // starting from the start position
  for pq.Len() > 0 {
    top := heap.Pop(&pq).([3]int)
    // Let's go through all positions that we can reach from
    for _, neighbor := range adjacencyList[[2]int{top[0], top[1]}] {
      travelCost := poscost[[2]int{top[0], top[1]}] + neighbor[2]
      nextCost, ok := poscost[[2]int{neighbor[0], neighbor[1]}]
      if !ok || travelCost < nextCost {
        poscost[[2]int{neighbor[0], neighbor[1]}] = travelCost
        heap.Push(&pq, neighbor)
      }
    }
  }
  return poscost[targetXY]
}


func dist(x1, y1, x2, y2 int) int {
  return abs(x2 - x1) + abs(y2 - y1)
}

func abs(a int) int {
  if a < 0 {
    return -a
  }
  return a
}
