using pi = pair<int, int>;
class Solution {
public:
    int distance(int x, int y, int x2, int y2) {
        return abs(x-x2) + abs(y-y2);
    }

    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        vector<vector<int>> roads;
        roads.push_back({ start[0], start[1], start[0], start[1], 0 });
        for (auto& r: specialRoads) {
            if (distance(r[0], r[1], r[2], r[3]) > r[4]) {
                roads.push_back(r);
            }
        }

        vector<int> dist(roads.size(), INT_MAX);
        // priority_queue<pi, vector<pi>, greater<>> q;
        queue<pi> q;
        q.push({ 0, 0 });
        dist[0] = 0;
        while (!q.empty()) {
            // auto [i, w] = q.top();
            auto [i, w] = q.front();
            q.pop();
            int x = roads[i][2], y = roads[i][3];
            for (int j = 0; j < roads.size(); j++) {
                vector<int>& r = roads[j];
                int startX = r[0], startY = r[1], endX = r[2], endY = r[3], w2 = r[4];
                int distToJ = distance(x, y, startX, startY) + w + w2;
                if (distToJ < dist[j]) {
                    dist[j] = distToJ;
                    q.push({ j, distToJ });
                }
            }
        }

        int ans = INT_MAX;
        for (int i = 0; i < roads.size(); i++) {
            vector<int> &r = roads[i];
            ans = min(ans, dist[i] + distance(r[2], r[3], target[0], target[1]));
        }

        return ans;
    }
};



