class Solution {
public:
    int djikstra(int source, int target, VVI &graph, VVI &edges){
        int n = graph.size();
        VI dis(n, INT_MAX);

        multiset<pair<int, int>> pq;
        pq.insert({source, 0});
        while(!pq.empty()){
            int node = pq.begin()->second, dist = pq.begin()->first;
