// Define a min-heap priority queue to store pairs of distance and index
template<class T> using MinPriorityQueue = priority_queue<T, vector<T>, greater<T>>;

class Solution {
public:

    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {

        const int INF = 1e9+10;
        int numSpecialRoads = specialRoads.size();

        // Initialize the distance of each special road to infinity
        vector<int> distance(numSpecialRoads, INF);

        // Create a priority queue and push the distance from start to each special road
        MinPriorityQueue<pair<int, int>> pq;
        for (int i = 0; i < numSpecialRoads; i++) {
            distance[i] = abs(start[0] - specialRoads[i][0]) + abs(start[1] - specialRoads[i][1]) + specialRoads[i][4];
            pq.push({distance[i], i});
        }

        // Initialize the answer with the Manhattan distance between start and target
        int minCost = abs(start[0] - target[0]) + abs(start[1] - target[1]);

        // Continue to search for the shortest path until the priority queue is empty
        while (!pq.empty()) {
            // Pop the pair with the smallest distance
            auto [currentDistance, currentRoad] = pq.top();
            pq.pop();

            // If the distance stored in 'distance' is not equal to the current distance 'currentDistance', skip this node
            if (currentDistance != distance[currentRoad])
                continue;

            // Update the answer by finding the distance from the current special road to the target
            minCost = min(minCost, currentDistance + abs(target[0] - specialRoads[currentRoad][2]) + abs(target[1] - specialRoads[currentRoad][3]));

            // For each special road that can be reached from the current special road, update its distance
            for (int nextRoad = 0; nextRoad < numSpecialRoads; nextRoad++) {
                int weight = abs(specialRoads[currentRoad][2] - specialRoads[nextRoad][0]) + abs(specialRoads[currentRoad][3] - specialRoads[nextRoad][1]) + specialRoads[nextRoad][4];
                if (currentDistance + weight < distance[nextRoad]) {
                    distance[nextRoad] = currentDistance + weight;
                    pq.push({distance[nextRoad], nextRoad});
                }
            }
        }

        // Return the minimum cost of reaching the target
        return minCost;
    }
};
