var minimumCost = function(start, target, specialRoads) {
    const startArgId = getId(start[0], start[1]);
    const targetArgId = getId(target[0], target[1]);
    const graph = buildGraph(start, target, specialRoads);

    const dp = {
        [startArgId]: 0,
        [targetArgId]: targetArgId === startArgId ? 0 : Number.MAX_SAFE_INTEGER,
    }

    const queue = new MinPriorityQueue({ priority: (e) => e.min });
    queue.enqueue({ node: startArgId, min: 0 });

    while(!queue.isEmpty()) {
        const { node, min: currentDistance } = queue.dequeue().element;

        if (node === targetArgId) {
            continue;
        }

        for (const neibor of Object.keys(graph[node])) {
            const distance = (dp[node] !== undefined ? dp[node] : Number.MAX_SAFE_INTEGER) + graph[node][neibor];

            if (dp[neibor] === undefined || distance < dp[neibor]) {
                dp[neibor] = distance;
                queue.enqueue({ node: neibor, min: dp[neibor] });
            }
        }
    }

    return dp[targetArgId];
};

