/**
 * @param {number[]} start
 * @param {number[]} target
 * @param {number[][]} specialRoads
 * @return {number}
 */
var minimumCost = function (start, target, specialRoads) {
  const filteredRoads = [];
  for (const road of specialRoads) {
    const [x1, y1, x2, y2, cost] = road;

    const normalCost = Math.abs(x1 - x2) + Math.abs(y1 - y2);
    if (cost < normalCost) {
      filteredRoads.push(road);
    }
  }

  const minHeap = new MinHeap([]);
  minHeap.insert([start[0], 0, start[1]]);
  const map = {};

  while (minHeap.getLength()) {
    const [x, costSoFar, y] = minHeap.remove();

    for (const road of filteredRoads) {
      const [x1, y1, x2, y2, cost] = road;

      const costToNextRoad = Math.abs(x - x1) + Math.abs(y - y1);
      const totalCost = costSoFar + costToNextRoad + cost;
      const key = [x2, y2].join('-');
      if (!(key in map) || map[key] > totalCost) {
        map[key] = totalCost;
        minHeap.insert([x2, totalCost, y2]);
      }
    }
  }

  let ans = Math.abs(start[0] - target[0]) + Math.abs(start[1] - target[1]);
  for (const [_, __, x2, y2, ___] of specialRoads) {
    const key = [x2, y2].join('-');
    if (!(key in map)) continue;

    const costToTarget = Math.abs(x2 - target[0]) + Math.abs(y2 - target[1]);
    ans = Math.min(ans, map[key] + costToTarget);
  }

  return ans;
};

